settings.custom_css:
.elementor-section.elementor-section-boxed > .elementor-container{
max-width: 100%;
}
.slider {

    height: 630px;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: 300px 300px;
    gap: 10px;
}

.slideritem:first-of-type {
    grid-row: 1/ span 2;
    height: 100%;
}


.slideritem {
    width: 100%;
    position: relative;
    overflow: hidden;


}

.slideritem img {
    box-shadow: 0 4px 40px rgb(119 132 157 / 10%);
    -webkit-box-shadow: rgb(119 132 157 / 10%) 0 4px 40px;

    width: 100%;
    object-fit: cover;
    height: 100%;
    object-position: bottom;
    transition: all 0.3s ease-out;
    border-radius: 4px;

}

.slideritem img:hover {

    transform: scale(1.1);
    transition: all 0.3s ease-out;
    opacity: 0.5;

}


.caption:hover .slideritem {
    opacity: 0.5;

}

.caption {
    color: grey;
    background-color: white;
    font-size: 2rem;
    padding: 5px 30px;
    position: absolute;
    top: 90%;
    left: 50%;
    -webkit-transition: all .12s ease-out;
    -moz-transition: all .12s ease-out;
    -o-transition: all .12s ease-out;
    transition: all .12s ease-out;
    transform: translate(-50%, -80%);
    width: 100%;
}


.slideritem:hover .caption {

    -moz-transform: translate(-50%, -90%);
    -webkit-transform: translate(-50%, -90%);
    -o-transform: translate(-50%, -90%);
    -ms-transform: translate(-50%, -90%);
    transform: translate(-50%, -90%);
    opacity: 1;
}

.mypostcontainer{
    position: sticky;
    top:0;
    box-shadow: 0 4px 40px rgb(119 132 157 / 10%) !important;
    -webkit-box-shadow: rgb(119 132 157 / 10%) 0 4px 40px !important;

}

.mypostcontainer .elementor-post__thumbnail__link{
    margin-bottom: 0 !important;
}


.mypostcontainer .elementor-post{
