settings.custom_css:
h1.entry-title{
    display: none;
}
.myelemenslider .elementor-container{
max-width: 80%;
} 
.slider {

    height: 630px;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: 300px 300px;
    gap: 10px;
}

.slideritem:first-of-type {
    grid-row: 1/ span 2;
    height: 100%;
}


.slideritem {
    width: 100%;
    position: relative;
    overflow: hidden;


}

.slideritem img {
    box-shadow: 0 4px 40px rgb(119 132 157 / 10%);
    -webkit-box-shadow: rgb(119 132 157 / 10%) 0 4px 40px;

    width: 100%;
    object-fit: cover;
    height: 100%;
    object-position: bottom;
    transition: all 0.3s ease-out;
    border-radius: 4px;

}

.slideritem img:hover {

    transform: scale(1.1);
    transition: all 0.3s ease-out;
    opacity: 0.5;

}


.caption:hover .slideritem {
    opacity: 0.5;

}

.caption {
    color: #111;
    background-color: white;
    font-size: 2rem;
    padding: 5px 30px;
    position: absolute;
    top: 90%;
    left: 50%;
    -webkit-transition: all .12s ease-out;
    -moz-transition: all .12s ease-out;
    -o-transition: all .12s ease-out;
    transition: all .12s ease-out;
    transform: translate(-50%, -80%);
    width: 100%;
}


.slideritem:hover .caption {

    -moz-transform: translate(-50%, -90%);
    -webkit-transform: translate(-50%, -90%);
    -o-transform: translate(-50%, -90%);
    -ms-transform: translate(-50%, -90%);
    transform: translate(-50%, -90%);
    opacity: 1;
}

:root {
    --speed: 0.65s;
}

@keyframes zoom {
    from {
        object-position: 100% 50%;
    }

    to {
        object-position: 0 50%;

    }
}

@keyframes blur {
    from {
        -webkit-transform: scale(1.5, 1.5);
        transform: scale(2, 2);
    }

    to {

        -webkit-transform: scale(1, 1);
        transform: scale(1, 1);

    }
}

.mypostswrapper {

    display: grid;
    width: 100%;
    padding-top: 40px;
    padding-bottom: 40px;
    justify-content: center;
    grid-template-columns: repeat(5, 1fr);
    grid-template-rows: max-content;
    justify-items: center;
    gap: 20px;
}


.card {
    position: relative;
    overflow: hidden;
    display: block;
    background: transparent;
    width: 100%;
    border-radius: 8px;
    box-shadow: 0 6px 10px rgb(0 0 0 / 30%);
    cursor: pointer;
    background-color: #111;
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
}

.card:hover {
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.5);
    -webkit-transform: scale(1.1, 1.1);
    transform: scale(1.1, 1.1);
}

.cardsave {
    position: absolute;
    top: 10px;
    right: 10px;
    display: block;
    width: 35px;
    height: 35px;
    background: transparent;
    border: 0;
    opacity: 0;
    border-radius: 3px;
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
}

.cardsave:focus {
    outline: 0;
    background-color: rgba(255, 255, 255, 0);
}

.card:hover .cardsave {
    opacity: 0.6;
}

.cardsave i {
    font-size: 18px;
    color: rgba(255, 255, 255, 0.6);
    cursor: pointer;
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
}

.cardsave:hover i {
    color: rgba(255, 255, 255, 0.8);
}

.cardsave:active,
.cardsave.active {
    opacity: 1 !important;
}

.cardsave:active i,
.cardsave.active i {
    color: white;
}

.cardimage {
    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;
    overflow: hidden;
    display: block;
    width: 100%;
    height: 100%;
    pointer-events: none;
    background: linear-gradient(to bottom, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0.5));
}

.cardimage img {
    -webkit-transform: scale(1.3, 1.3);
    transform: scale(1.3, 1.3);
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    opacity: 1;
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
    animation-name: zoom;
    animation-duration: 30s;
    animation-direction: alternate;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
}

.card:hover .cardimage img {
    -webkit-transform: scale(1, 1);
    transform: scale(1, 1);
    opacity: 0.5;
}

.cardheader {
    display: grid;
    width: 100%;
    height: 200px;
}

.cardprofile {
    align-self: center;
    justify-self: center;
    display: block;
    overflow: hidden;
    width: 150px;
    height: 150px;
    border-radius: 50%;
}

.cardprofile img {
    -webkit-transform: scale(1.5, 1.5) translateZ(0);
    transform: scale(1.5, 1.5) translateZ(0);
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: 50% 50%;
    -webkit-filter: grayscale(50%) contrast(75%) brightness(1.3);
    filter: grayscale(50%) contrast(75%) brightness(1.3);
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
    mix-blend-mode: normal;
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
}

.card:hover .cardprofile img {
    opacity: .5;
    -webkit-transform: scale(1, 1) translateZ(0);
    transform: scale(1, 1) translateZ(0);
}

.cardbody {
    display: grid;
    padding: 0 20px;
}

.cardname {
    align-self: center;
    justify-self: center;
    margin-bottom: 2px;
    color: white;
    letter-spacing: 0.1rem;
    text-align: center;
    font-size: 1.5rem;
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.35);
    background: white;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
}

.card:hover .cardname {
    background: white;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.cardjob {
    align-self: center;
    justify-self: center;
    display: inline-block;
    text-align: center;
    letter-spacing: 0.35rem;
    color: rgba(100, 130, 200, 0.45);
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
}

.card:hover .cardjob {
    color: rgba(100, 130, 200, 1);
}

.cardbio {
    position: relative;
    -webkit-transform: translateY(30%);
    transform: translateY(30%);
    display: block;
    margin: 22px 0 16px 0;
    color: rgba(255, 255, 255, 0.65);
    opacity: 0;
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
}

.card:hover .cardbio {
    -webkit-transform: translateY(0);
    transform: translateY(0);
    opacity: 1;
}

.cardfooter {
    position: relative;
    -webkit-transform: translateY(60%);
    transform: translateY(60%);
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-areas: "date category";
    padding: 10px 20px;
    opacity: 0;
    -webkit-transition: all var(--speed) ease;
    transition: all var(--speed) ease;
}

.card:hover .cardfooter {
    -webkit-transform: translateY(0);
    transform: translateY(0);
    opacity: 1;
}

.carddate {
    grid-area: date;
    display: inline-block;
    align-self: left;
    justify-self: left;
    text-transform: uppercase;
    text-shadow: 0 0.5px 1px rgba(0, 0, 0, 0.45);
    color: rgba(250, 250, 250, 0.4);
}

/* MODIFIERS */
.card[data-effect="blur"]:hover .cardimage img {
    -webkit-filter: blur(3px);
    filter: blur(3px);
}

.card[data-effect="blur"] .cardimage img {
    animation-name: blur;
}

.card[data-effect="color"]:hover {
    -webkit-transform: scale(1, 1);
    transform: scale(1, 1);
    box-shadow: 0 40px 260px rgba(255, 0, 0, 0.1), 0 40px 130px rgba(250, 100, 100, 0.2), -80px -40px 230px rgba(0, 200, 250, 0.15), 80px 40px 230px rgba(120, 120, 255, 0.15);
}

.card[data-effect="color"] .cardimage {
    background: linear-gradient(to bottom, rgba(20, 20, 100, 1), rgba(255, 100, 100, 0.5))
}

.card[data-effect="color"]:hover .cardimage img {
    opacity: 0.8;
}

.card[data-effect="color"]:hover .cardprofile img {
    -webkit-filter: grayscale(50%) contrast(80%) brightness(1.6);
    filter: grayscale(50%) contrast(80%) brightness(1.6);
    mix-blend-mode: normal;
}


.customqueryv2contain {

    width: 100%;
    flex-wrap: wrap;
    display: flex;
        justify-content: space-between;
}

.customqueryv2 {

    width: 48%;
